AssemblyLine.PrologInit:
// Get the task parameter for the last changenumber (subtract 0 to 
// convert from JS String to number).
//
try 
{
	lastChangeNum = task.getParam("LastChangeNum") - 0;
}
catch (msg)
{
	lastChangeNum = 0;
}

// Set up the Changelog iterator
//
timldapchangelog.connector.setParam("nsChangenumber", lastChangeNum + 1);
task.logmsg("");
task.logmsg("---> Starting Changelog processing at changenumber : " + timldapchangelog.connector.getParam("nsChangenumber"));

Hook.Script:
//filter out the TIM user changes only

var tdn=conn.getString("targetdn");
if ((!tdn.startsWith("ERGLOBALID"))&&(!tdn.startsWith("ergloblid")))
    system.skipEntry();
else{ 
  	task.setParam("LastChangeNum", conn.getString("changenumber"));
	task.logmsg("---> Changenumber to process and save : " +
			            	task.getParam("LastChangeNum"));
    task.logmsg("---> TIM user change found. The TIM DN is: " + tdn);
}

//filter out the orphans account

var pdn=conn.getString("$dn");
if (pdn.indexOf("ou=orphans",0)> 0)
    system.skipEntry();
else     
    task.logmsg("---> TAM account found and the TAM DN is: " +
			            	conn.getString("ertamdn"));

var pdn=conn.getString("$dn");
if (pdn.indexOf("ou=orphans",0)> 0)
     system.skipEntry();
else{
   task.logmsg("---> TAM account found and the TAM DN is: " +
			            	conn.getString("ertamdn"));
   task.logmsg("---> The DN of TIM user who owns this TAM account is: " +
			            	conn.getString("erparent"));
}
